import React,{Fragment,Component} from 'react';

class Header extends Component{
    constructor(){
        super();
        this.state={
            title: 'Edureka react with redux course'
        }
    }
    render(){
        return(
            <Fragment>
            <header>
                <center>
                    <h2>{this.state.title}</h2>
                </center>
            </header>
            <hr/>
        </Fragment>
        )
    }
}

