import React from 'react';
import { Link } from 'react-router-dom';

const CourseList = (props) => {

    const renderList = ({ datalist }) => {
        if (datalist) {
            return datalist.map((item) => {
                return (
                    // // <Link to={`/Courses/${item.id}`} key={item.id}>
                    // // <div>
                    // // <label>{item.title}</label>
                    // // </div>
                    // // </Link>
                    <div>
                        <label>Course title:</label>&nbsp;{item.title}<br />
                        <label>Course Author:</label>&nbsp;{item.author}<br />
                        <label>Couse Description:</label>&nbsp;{item.description}<br />
                        <Link to="/EnquireForm"><button>Enquire</button></Link>
                        <hr />
                    </div>
                )
            })
        }
    }

    return (
        <div>
            {renderList(props)}
        </div>
    )
}

export default CourseList;
