import React,{Component} from 'react';

class Task_Form extends Component{

    constructor(){
        super()
        this.state={
            tasks: ''
        }
    }

    handleInputChange=(event)=>{
        console.log(event.target.name);
        console.log(event.target.checked)
        this.setState({tasks:event.target.name})
    }

    render(){
        return(
            <form>
        <label>
          Eat
          <input
            name="Eat"
            type="checkbox"
            checked={this.state.eat}
            onChange={this.handleInputChange} />
        </label><br/>
        <label>
          Read
          <input
            name="Read"
            type="checkbox"
            checked={this.state.read}
            onChange={this.handleInputChange} />
        </label><br/>
        <label>
          Sleep
          <input
            name="Sleep"
            type="checkbox"
            checked={this.state.sleep}
            onChange={this.handleInputChange} />
        </label><br/>
        <label>
          Repeat
          <input
            name="Repeat"
            type="checkbox"
            checked={this.state.repeat}
            onChange={this.handleInputChange} />
        </label><br/>
        {this.state.tasks}
       <button onClick={this.handlesubmit}>
           DONE
       </button>

      </form>
        )
    }
}


export default Task_Form;
